function all_or_not(sentence,words){
  a = words.length
  occ = 0
  not = 0
  already = []
  tabs = sentence.split(' ')
  words.forEach(function(element){
    element = element.toLowerCase()
    if (!already.includes(sentence) && tabs.includes(element)){
      occ+=1
      already.push(element)
    }
    else{
      not+=1
    }
  });
if (occ > not ){
    for(var j=a;j>=0;j--){
      if((occ-j) == 0)
        return true
    }
  }
}


