function checking(list_sentence, list_word){
  occurences = []
  indexes = []
  index=0
  list_sentence.forEach(function(sentence){
    occurence = 0
    indexes.push(index)
    index+=1
    list_word.forEach(function(word){
      i = sentence.includes(word)
      if (i != -1){
        occurence+=1
      }
    });
    occurences.push(occurence)
  });
  right = get_maxs(occurences,3)
  return right
  //return occurences
}

function get_maxs(mylist,nmax){
  indices = []
  for(var j=0;j <= nmax;j++){
    i = mylist.indexOf(Math.max(...mylist))
    indices.push(i)
    //mylist= mylist.filter(item => item != Math.max(...mylist))
    mylist[i] = -1
  }
  return indices;
}






















































